    def setupMapView(self):
        """Set up the map view with QWebEngineView"""
        try:
            # Create profile settings for QWebEngineView
            profile = QWebEngineProfile.defaultProfile()
            profile.setPersistentCookiesPolicy(QWebEngineProfile.NoPersistentCookies)
            profile.setHttpCacheType(QWebEngineProfile.MemoryHttpCache)
            
            # Create web view for map
            self.map_widget = QWebEngineView(self.mapLabel)
            
            # Set web page settings
            settings = self.map_widget.settings()
            settings.setAttribute(QWebEngineSettings.JavascriptEnabled, True)
            settings.setAttribute(QWebEngineSettings.LocalContentCanAccessRemoteUrls, True)
            settings.setAttribute(QWebEngineSettings.LocalContentCanAccessFileUrls, True)
            settings.setAttribute(QWebEngineSettings.AllowGeolocationOnInsecureOrigins, True)
            settings.setAttribute(QWebEngineSettings.ShowScrollBars, False)
            settings.setAttribute(QWebEngineSettings.LocalStorageEnabled, True)
            
            # Set size and position
            self.map_widget.resize(self.mapLabel.size())
            self.map_widget.hide()  # Will be shown when map is loaded
            
            # Create info label
            self.info_label = QtWidgets.QLabel(self.mapLabel)
            self.info_label.setText("Harita sunucusunun başlaması bekleniyor...")
            self.info_label.setStyleSheet("background-color: rgba(255,255,255,180); color: black; padding: 5px;")
            self.info_label.setAlignment(QtCore.Qt.AlignTop)
            self.info_label.setFixedHeight(40)
            self.info_label.setFixedWidth(self.mapLabel.width())
            self.info_label.show()
            
        except Exception as e:
            print(f"Map setup error: {e}")
            self.mapLabel.setText(f"Harita yüklenemedi: {str(e)}")
            self.mapLabel.setStyleSheet("color: red; font-size: 12pt;")

